import {useState} from 'react'

// Custom Hook to alert React that token retrieval was successful, by triggering a re-render when data changes
export default function useToken() {

    // Retrieve token to render correct page
    const getToken = () => {
        const userTokenRetrievedString = sessionStorage.getItem('userTokenStored')     // Retrieve token using Key
        const userTokenRetrieved = JSON.parse(userTokenRetrievedString)                        // Convert to JSON
        return userTokenRetrieved?.userTokenStoredKey   // Chaining operator, since userTokenStoredKey is invalid for first session
    }

    // State initialised with retrieveToken
    const[userTokenStored, setToken] = useState(retrieveToken())    // useState here triggers re-render

    // Session Storage (for Authentication) + updating State
    // storedToken is placeholder for function below it
    const storedToken = (userToken) => {
        sessionStorage.setItem('userTokenStored', JSON.stringify(userToken))   // Saving to Session Storage
        setToken(userToken)                                                    // Updating Stateful Value
    }

    // Return an Object
    return {
        returnToken : userTokenStored, storedToken
    }


}
