// Each input will have it's own COMPONENT level state, not APP level state
import {useState} from 'react'


const AddTask = function( {onAdd} )
{
    const[task, setTask] = useState(' ')
    const[date, setDate] = useState(' ')
    const[reminder, setReminder] = useState(false)

    return(
        <form className = 'add-form' onSubmit >

            <div className = 'form-control'>
                <label htmlFor = 'Task'>
                    Task
                </label>
                <input type = 'text'
                       name = 'Task'
                       placeholder = 'Add Tasks'
                       onChange = { (e) => setTask(e.target.value) }
                />
            </div>

            <div className = 'form-control'>
                <label htmlFor='Date'>
                    Date
                </label>
                <input type='text'
                       name='Date'
                       placeholder = 'Enter Date'
                       onChange = { (e) => setDate(e.target.value) }
                />
            </div>

            <div className = 'form-control form-control-check'>  {/* Input multiple classes by using Quotations, can use String Template also */}
                <label htmlFor='Reminders'>
                    Reminders
                </label>
                <input type='checkbox'
                       name='Reminders'
                       onChange = { (e) => setReminder(e.target.checked) }
                />
            </div>

            <input type = 'submit' value = 'Save Task' className = 'btn btn-block' />
        </form>
    )
}

export default AddTask
