import Task from './Task'

// Destructuring tasks passed in from App.js
const Tasks = function( {tasks} )
{
    return(
        <>
            {
                tasks.map(           /* Looping through each index in tasks Object Array*/
                    (task) => (      /* task is where new Array will be stored */
                        /* Returns a user-defined Task Component */
                        <Task
                            key = {task.id}  /* Store each task.id into key variable */
                            task = {Task}    /* Pass in Task as Prop into Task.js, into variable task */
                        />
                    )
                )
            }
        </>
    )
}

export default Tasks
