import React, { useState } from "react";
import format from "date-fns/format"
import subMonths from "date-fns/subMonths"
import startOfWeek from 'date-fns/startOfWeek'
import endOfWeek from 'date-fns/endOfWeek'
import addDays from 'date-fns/addDays'
import startOfMonth from 'date-fns/startOfMonth'
import endOfMonth from 'date-fns/endOfMonth'
import isSameMonth from 'date-fns/isSameMonth'
import isSameDay from 'date-fns/isSameDay'
import addMonths from 'date-fns/addMonths'
import toDate from 'date-fns/toDate'
import "./Calendar.css";

const Calendar = () => {

    // Default State is current Day
    const [currentDate, setCurrentDate] = useState(new Date());     // for Calendar to render proper month
    const [selectedDate, setSelectedDate] = useState(new Date());   // styling for selected Date

    // Add 1 to current Month
    const nextMonth = () => {
        setCurrentDate(addMonths(currentDate, 1));
    };

    // Deduct 1 from current Month
    const prevMonth = () => {
        setCurrentDate(subMonths(currentDate, 1));
    };

    const header = () => {
        const dateFormat = "MMMM yyyy";    // sets how Header is displayed
        return (

            <div className="header row flex-middle">
                <div className="column col-start">
                    <div className="icon" onClick={prevMonth}>
                        chevron_left
                    </div>
                </div>

                <div className="column col-center">
                    <span>{format(currentDate, dateFormat)}</span>
                </div>

                <div className="column col-end">
                    <div className="icon" onClick={nextMonth}>
                        chevron_right
                    </div>
                </div>
            </div>
        );
    };


    const days = () => {
        const dateFormat = "E";                     // sets how each day of week is displayed
        const days = [];
        let startDate = startOfWeek(currentDate);   // use date-fns startOfWeek along with currentDate state



        for (let i = 0; i < 7; i++) {
            days.push(
                <div className="column col-center" key={i}>
                    {format(addDays(startDate, i), dateFormat)}
                </div>
            );
        }
        return <div className="days row">{days}</div>;
    };
    const cells = () => {
        const monthStart = startOfMonth(currentDate);
        const monthEnd = endOfMonth(monthStart);
        const startDate = startOfWeek(monthStart);
        const endDate = endOfWeek(monthEnd);
        const dateFormat = "d";
        const rows = [];
        let days = [];
        let day = startDate;
        let formattedDate = "";
        while (day <= endDate) {
            for (let i = 0; i < 7; i++) {
                formattedDate = format(day, dateFormat);
                const cloneDay = day;
                days.push(
                    <div
                        className={`column cell ${!isSameMonth(day, monthStart)
                            ? "disabled" : isSameDay(day, selectedDate)
                                ? "selected" : "" }`}
                        key={day}
                        onClick={() => onDateClick(toDate(cloneDay))}
                    >
                        <span className="number">{formattedDate}</span>
                        <span className="bg">{formattedDate}</span>
                    </div>
                );
                day = addDays(day, 1);
            }
            rows.push(
                <div className="row" key={day}> {days} </div>
            );
            days = [];
        }
        return <div className="body">{rows}</div>;
    }


    const onDateClick = day => {
        setSelectedDate(day);
    }

    // Rendering
    return (
        <div className="calendar">
            <div>{header()}</div>
            <div>{days()}</div>
            <div>{cells()}</div>
        </div>
    );
};
export default Calendar;
